ll n,k;
ll gt(ll x){
    int i=upper_bound(pos+1,pos+maxn+1,x)-pos-1;
    ll ds=a[i];
    for(ll j=pos[i]+1;j<=x;j++) ds=(ds*j)%mod;
    return ds;
}
ll bpow(ll u,ll v){
    ll ds=1;
    while(v>0){
        if(v%2) ds=(ds*u)%mod;
        u=(u*u)%mod;
        v/=2;
    }
    return ds;
}
ll ckn(ll u,ll v){
    ll x=gt(u);
    ll ts=gt(v);
    ll z=gt(v-u);
    ll ms=(x*z)%mod;
    return (ts*bpow(ms,mod-2))%mod;
}
